              {
                $mhd = $v2['value'];
                $belegesnarr['mhd'][$k2]['menge'] -= 1;
                break;
              }elseif($v2['menge'] == 1)
              {
                $mhd = $v2['value'];
                unset($belegesnarr['mhd'][$k2]);
                break;
              }
            }
            if(isset($v['table']) &&  $v['table'] == 'seriennummern' && !empty($v['id']))
            {
              $this->app->DB->Delete("DELETE FROM seriennummern WHERE id = '".$v['id']."' LIMIT 1");
