    $belege_check = array();

    switch($type)
    {
      case "rechnung":
        $belege_check['rechnung']=$id;
        $belege_check['lieferschein']=$this->app->DB->Select("SELECT lieferschein FROM rechnung WHERE id='$id' LIMIT 1");
