      $this->app->DB->Update("UPDATE $module SET bearbeiter='".$buchhaltung."' WHERE id='$id' LIMIT 1");
    }
  }

  // @refactor in Lager Modul
  function GetArtikelStandardlager($artikel,$send_id=false)
  {
    if($send_id)
