// @refactor Cache Komponente
function ClearSqlCache($shortcode, $seconds = 0)
{
  if($shortcode == '') {
    if($seconds > 0) {
      $this->app->DB->Delete(
        sprintf(
          'DELETE FROM sqlcache WHERE DATE_DIFF(zeitstempel, INTERVAL %d SECOND) < NOW()',
          $seconds
        )
      );
      return;
    }
    $this->app->DB->Delete('DELETE FROM sqlcache');
    return;
  }
  if($seconds > 0) {
    $this->app->DB->Delete(
      sprintf(
        "DELETE FROM sqlcache WHERE shortcode = '%s' AND DATE_DIFF(zeitstempel, INTERVAL %d SECOND) < NOW()",
        $this->app->DB->real_escape_string($shortcode), $seconds
      )
    );
    return;
  }
  $this->app->DB->Delete(
    sprintf(
      "DELETE FROM sqlcache WHERE shortcode = '%s'",
      $this->app->DB->real_escape_string($shortcode)
    )
  );
}

