      $this->InstallModul($module);
      if($onlyInstallFromKey > 0) {
        $this->app->erp->SetKonfigurationValue('moduleupdatenextkey', $onlyInstallFromKey + 1);
        $onlyInstallFromKey++;
      }
    }
    $cModules = count($modules);
