  /**
   * @param array  $faqs
   * @param string $target
   */
  public function showInlineFaq($faqs, $target = 'INLINEHELP2')
  {
    $html = '<div id="faqaccordion">';
    if(!empty($faqs))  {
      foreach($faqs as $faq) {
        $html .= '<div class="group"><h2>'.$faq['question'].'</h2><div>'.$faq['answer'].'</div></div>';
      }
    }
    $html .= '</div>';
    $this->app->Tpl->Add($target, $html);
  }

