  // @refactor Formater Komponente
  function FormatMengeBetrag($value)
  {
    if(strpos($value,'.') !== false)
    {
      $pos = strpos($value,'.');
      $value = rtrim($value,'0');
      if(strlen($value) <= $pos + 2) {
        $value .= '0';
      }
      if(strlen($value) <= $pos + 2) {
        $value .= '0';
      }
    }else {
      $value .= '.00';
    }
    if($value[0] === '.') {
      $value = '0'.$value;
    }
    return $value;
  }

  // @refactor FormHelper Komponente
  function ReplaceMengeBetrag($db,$value,$fromform)
  {
    $dbformat = 0;
    if(strpos($value,'.') > 0) {
      $dbformat = 1;
    }

    // wenn ziel datenbank
    if($db)
    {
      if($dbformat) {
        return $value;
      }
      if($value!=''){
        return str_replace(',', '.', $value);
      }
      return '';
    }
    // wenn ziel formular

    $value = str_replace('.',',',$this->FormatMengeBetrag($value));
    if($dbformat) {
      if($value!='') {
        return $value;
      }
      return '';
    }
    return $value;
  }

  // @refactor FormHelper Komponente
  function ReplaceMenge($db,$value,$fromform)
  {
    $tcheck = str_replace(',','.',$value);
    if($tcheck < 0) {
      return 1;
    }


    $dbformat = 0;
    if(strpos($value,'.') > 0) {
      $dbformat = 1;
    }

    // wenn ziel datenbank
    if($db)
    {
      if($dbformat) {
        return $value;
      }

      if($value!=''){
        return str_replace(',', '.', $value);
      }

      return '';
    }
    // wenn ziel formular
    if(strpos($value,'.') !== false)
    {
      $value = rtrim(rtrim($value,'0'),'.');
      if($value[0] === '.') {
        $value = '0'.$value;
      }
    }
    if($dbformat) {
      if($value!='') {
        return str_replace('.',',',$value);
      }
      return '';
    }

    return $value;
  }

