    }

    if($tracking_again=="1")
    {
      $error_message_tracking = "<div class=\"error\">Fehlerhafte Trackingnummer!</div>";
    }

      if($sid=="versand")
      {
        $paketmarkedrucken = "";//"<input type=\"button\" name=\"paketmarkedrucken\" value=\"Paketmarke nochmal drucken\" onclick=\"window.location.href='index.php?module=versanderzeugen&action=korrektur&id=$id'\">";
      }
      else $paketmarkedrucken="";

    //header("Location: index.php?module=paketmarke&action=tracking");
    $this->app->Tpl->Add('TAB1',
        "<br>$error_message_tracking<br><table width=\"60%\" style=\"background-color: #fff; border: solid 1px #000;\" align=\"center\">
        <tr>
        <td align=\"center\">
        <br><br>
        <h2>Tracking-Nummer Scannen:</h2><br><br>
        <form action=\"\" method=\"post\">Tracking-Nummer: <input name=\"tracking\" type=\"text\" id=\"tracking\"><script type=\"text/javascript\">document.getElementById(\"tracking\").focus(); </script>&nbsp;<input type=\"submit\" name=\"trackingsubmit\" value=\"Speichern\">
        <br><br>
<center>
          <table>
          <tr><td><input type=\"checkbox\" name=\"weiterespaket\" value=\"1\" onclick=\"document.getElementById('tracking').focus()\"></td><td>&nbsp;Weitere Paketmarke f&uuml;r diese Lieferung erstellen.</td></tr>
          <tr><td><input type=\"checkbox\" name=\"lieferscheinkopie\" value=\"1\" onclick=\"document.getElementById('tracking').focus()\"></td><td>&nbsp;Zus&auml;tzlichen Lieferschein drucken.</td></tr>
          </table>
          </center>
<br>
<center>
          <br>
          $paketmarkedrucken
          <input type=\"submit\" name=\"trackingsubmitcancel\" value=\"Keine Tracking-Nummer erfassen\">
          </center>
</form>
        </td></tr></table>
        <br><br>
        ");

  } else {

    if($sid=="rechnung")
      $rechnung = $id;
    else $rechnung ="";

    if($sid=="versand")
    {
      $tid = $this->app->DB->Select("SELECT lieferschein FROM versand WHERE id='$id' LIMIT 1");
      $rechnung  = $this->app->DB->Select("SELECT rechnung FROM versand WHERE id='$id' LIMIT 1");
      $sid = "lieferschein";
    } else {
      $tid = $id;
    }


    if($sid=="rechnung" || $sid=="lieferschein" || $sid=="adresse")
    {
      $name = $this->app->DB->Select("SELECT name FROM $sid WHERE id='$tid' LIMIT 1");
      $name2 = $this->app->DB->Select("SELECT adresszusatz FROM $sid WHERE id='$tid' LIMIT 1");
      if($name2=="")
      {
        $name2 = $this->app->DB->Select("SELECT abteilung FROM $sid WHERE id='$tid' LIMIT 1");
        $abt=1;
      }
      $name3 = $this->app->DB->Select("SELECT ansprechpartner FROM $sid WHERE id='$tid' LIMIT 1");
      if($name3=="" && $abt!=1)
        $name3 = $this->app->DB->Select("SELECT abteilung FROM $sid WHERE id='$tid' LIMIT 1");

      //unterabteilung versuchen einzublenden
      if($name2=="") {
        $name2 = trim($this->app->DB->Select("SELECT unterabteilung FROM $sid WHERE id='$tid' LIMIT 1"));
      } else if ($name3=="") {
        $name3 = trim($this->app->DB->Select("SELECT unterabteilung FROM $sid WHERE id='$tid' LIMIT 1"));
      }



      $ort = $this->app->DB->Select("SELECT ort FROM $sid WHERE id='$tid' LIMIT 1");
      $plz = $this->app->DB->Select("SELECT plz FROM $sid WHERE id='$tid' LIMIT 1");
      $land = $this->app->DB->Select("SELECT land FROM $sid WHERE id='$tid' LIMIT 1");
      $strasse = $this->app->DB->Select("SELECT strasse FROM $sid WHERE id='$tid' LIMIT 1");
      $hausnummer = $this->ExtractStreetnumber($strasse);

      $strasse = str_replace($hausnummer,"",$strasse);
      $strasse = str_replace('.',"",$strasse);

      if($strasse=="")
      {
        $strasse = $hausnummer;
        $hausnummer = "";
      }

    }

    // wenn rechnung im spiel entweder durch versand oder direkt rechnung
    if($rechnung >0)
    {

      $zahlungsweise =  $this->app->DB->Select("SELECT zahlungsweise FROM rechnung WHERE id='$rechnung' LIMIT 1");
      $soll =  $this->app->DB->Select("SELECT soll FROM rechnung WHERE id='$rechnung' LIMIT 1");
      $this->app->Tpl->Set('BETRAG',$soll);

      if($zahlungsweise=="nachnahme")
        $this->app->Tpl->Set('NACHNAHME',"checked");

      if($soll >= 500 && $soll <= 2500)
        $this->app->Tpl->Set('VERSICHERT',"checked");

      if($soll > 2500)
        $this->app->Tpl->Set('EXTRAVERSICHERT',"checked");

    }
    $this->app->Tpl->Set('NAME',$name);
    $this->app->Tpl->Set('NAME2',$name2);
    $this->app->Tpl->Set('NAME3',$name3);
    $this->app->Tpl->Set('ORT',$ort);
    $this->app->Tpl->Set('PLZ',$plz);
    $this->app->Tpl->Set('STRASSE',$strasse);
    $this->app->Tpl->Set('HAUSNUMMER',$hausnummer);
    $this->app->Tpl->Set('LAND',$land);

    $abholdatum_user = $this->app->User->GetParameter("paketmarke_abholdatum");
    $abholdatum_user = date('Y-m-d', strtotime($abholdatum_user));

    $check_date = $this->app->DB->Select("SELECT date_format(now(),'%Y-%m-%d')");
    if($abholdatum_user >= $check_date)
      $this->app->Tpl->Set('ABHOLDATUM',date('d.m.Y', strtotime($abholdatum_user)));
    else
      $this->app->Tpl->Set('ABHOLDATUM',date('d.m.Y', strtotime($check_date)));

//    $this->app->Tpl->Set('ABHOLDATUM',$this->app->DB->Select("SELECT date_format(now(),'%d.%m.%Y')"));


    $anzahl = $this->app->Secure->GetGET("anzahl");

    if($anzahl <= 0) $anzahl=1;

    $this->app->Tpl->Set('ANZAHL',$anzahl);

    $standardkg=$this->VersandartMindestgewicht($lieferschein);
    $this->PaketmarkeGewichtForm($anzahl, $standardkg, $this->VersandartMindestgewicht());
    /*
    if($anzahl==1)
    {
      $i=1;
      $this->app->Tpl->Add('GEWICHT','<tr><td nowrap>Gewicht Paket:</td><td><input type="text" name="kg'.$i.'" size="5" value="'.$standardkg.'">&nbsp;<i>in kg</i></td></tr>');
    }
    else {
      for($i=1;$i<=$anzahl;$i++)
        $this->app->Tpl->Add('GEWICHT','<tr><td nowrap>Gewicht Paket '.$i.':</td><td><input type="text" name="kg'.$i.'" size="5" value="'.$standardkg.'">&nbsp;<i>in kg</i></td></tr>');
    }*/


    if($tracking_again!="1")
      $this->app->Tpl->Parse($parsetarget,"paketmarke_dpd.tpl");
  }
}

//@refactor versanddiestleister Modul
function PaketmarkeGewichtForm($anzahl, $standardkg, $mindestgewicht = 0)
{
  $mitwaage = false;
  $mindestgewicht = (float)$mindestgewicht;
  if($this->app->erp->Firmendaten('paketmarke_mit_waage'))
  {
    $mitwaage = $this->app->DB->Select("SELECT seriennummer FROM adapterbox WHERE verwendenals = 'waage' AND seriennummer <> '' LIMIT 1");
  }
  if($anzahl==1)
  {
    $i=1;
    $this->app->Tpl->Add('GEWICHT','<tr><td nowrap>Gewicht:</td><td><input type="text" id="kg'.$i.'" name="kg'.$i.'" size="5" value="'.$standardkg.'">&nbsp;<i>in kg</i>');
    if($mitwaage)
    {
      $this->app->Tpl->Add('GEWICHT','&nbsp;<input type="button" value="Waage" onclick="Getgewicht('.$i.')" />');
      $this->app->Tpl->Set('SERIENNUMMER', $mitwaage);
      $this->app->Tpl->Set('MINDESTGEWICHT', $mindestgewicht);
      $this->app->Tpl->Parse('GEWICHT', 'paketmarke_waage.tpl');
    }
    $this->app->Tpl->Add('GEWICHT','</td></tr>');

  }
  else {
    for($i=1;$i<=$anzahl;$i++)
    {
      $this->app->Tpl->Add('GEWICHT','<tr><td nowrap>Gewicht:</td><td><input type="text" id="kg'.$i.'" name="kg'.$i.'" size="5" value="'.$standardkg.'">&nbsp;<i>in kg</i>');
      if($mitwaage)
      {
        $this->app->Tpl->Add('GEWICHT','&nbsp;<input type="button" value="Waage" onclick="Getgewicht('.$i.')" />');
        $this->app->Tpl->Set('SERIENNUMMER', $mitwaage);
        $this->app->Tpl->Set('MINDESTGEWICHT', $mindestgewicht);
        if($i == 1)$this->app->Tpl->Parse('GEWICHT', 'paketmarke_waage.tpl');
      }
      $this->app->Tpl->Add('GEWICHT','</td></tr>');
    }
  }
}

/**@deprecated */
function PaketmarkeDHLEmbedded($parsetarget,$sid="",$zusatz="")
{
  $id = $this->app->Secure->GetGET("id");
  // entscheiden ob Intraship oder Easylog anhang Projekt einstellung
  // wenn sid==versand dann steht die id in der vesandtabelle
  if($sid=="versand")
  {
    $lieferschein = $this->app->DB->Select("SELECT lieferschein FROM versand WHERE id='$id' LIMIT 1");
    $projekt = $this->app->DB->Select("SELECT projekt FROM lieferschein WHERE id='$lieferschein' LIMIT 1");
  } else {
    // ansonsten ist es die id vom lieferschein
    $projekt = $this->app->DB->Select("SELECT projekt FROM lieferschein WHERE id='$id' LIMIT 1");
  }

  //TODO pruefe ob i oder easylog
  $intraship_enabled = $this->app->DB->Select("SELECT intraship_enabled FROM projekt WHERE id='$projekt' LIMIT 1");
  if($intraship_enabled=="1")
    $this->Paketmarke($parsetarget,$sid,$zusatz,"Intraship");
  else
    $this->Paketmarke($parsetarget,$sid,$zusatz,"DHL");

}
//@refactor versanddiestleister Modul
function VersandartMindestgewicht($id=0)
{
  $gewicht = $this->LieferscheinNettoGewicht($id);
  $versandart =""; $projekt=0; $intraship_weightinkg=0;
  $gewicht /= $this->GewichtzuKgFaktor();

if($id > 0)
{
  $versandart = $this->app->DB->Select("SELECT versandart FROM lieferschein WHERE id='$id' LIMIT 1");
  $projekt = $this->app->DB->Select("SELECT projekt FROM lieferschein WHERE id='$id' LIMIT 1");
  $intraship_weightinkg = $this->app->DB->Select("SELECT intraship_weightinkg FROM projekt WHERE id='$projekt' LIMIT 1");
  $versandart = strtolower($versandart);

  $modul = $this->app->DB->SelectArr("SELECT id, modul FROM `versandarten` WHERE aktiv = 1 AND ausprojekt = 0 AND modul <> '' AND type = '".$this->app->DB->real_escape_string($versandart)."' AND geloescht = 0 AND (projekt = 0 OR projekt = '$projekt') ORDER by projekt = '$projekt' DESC LIMIT 1");
  if($modul && @is_file(dirname(__FILE__).'/versandarten/'.$modul[0]['modul'].'.php'))
  {
    $obj = $this->LoadVersandModul($modul[0]['modul'], $modul[0]['id']);
    if($obj){
      if(method_exists($obj, 'VersandartMindestgewicht'))
        $mindestgewicht = $obj->VersandartMindestgewicht();
      return $mindestgewicht > $gewicht ? $mindestgewicht : $gewicht;
    }
  }
}

  if(($versandart=="dhl" || $versandart=="versandunternehmen") && $intraship_weightinkg > 0 && $gewicht <=0) return $intraship_weightinkg;
  else if ($gewicht > 0) return $gewicht;
  else return 2;
}

//@refactor ApplicationCore
function LoadVersandModul($modul, $id)
{
  if(empty($modul) ||
    strpos($modul,'..') !== false ||
    !@is_file(__DIR__.'/versandarten/'.$modul.'.php')
  )
  {
    return;
  }
  $class_name = 'Versandart_'.$modul;
  $class_name_custom = 'Versandart_'.$modul.'Custom';
  if(!class_exists($class_name))
  {
    if(@is_file(__DIR__.'/versandarten/'.$modul.'_custom.php'))
    {
      include_once(__DIR__.'/versandarten/'.$modul.'_custom.php');
    }else{
      include_once(__DIR__.'/versandarten/'.$modul.'.php');
    }
  }
  if(class_exists($class_name_custom))
  {
    return new $class_name_custom($this->app, $id);
  }elseif(class_exists($class_name))
  {
    return new $class_name($this->app, $id);
  }
}

//@refactor versanddiestleister Modul
function Paketmarke($parsetarget,$sid="",$zusatz="",$typ="DHL")
{
  if($sid=="")
    $sid= $this->app->Secure->GetGET("sid");

  if($zusatz=="express")
    $this->app->Tpl->Set('ZUSATZ',"Express");

  if($zusatz=="export")
    $this->app->Tpl->Set('ZUSATZ',"Export");

  $id = $this->app->Secure->GetGET("id");
  $drucken = $this->app->Secure->GetPOST("drucken");
  $anders = $this->app->Secure->GetPOST("anders");
  $land = $this->app->Secure->GetGET("land");
  $tracking_again = $this->app->Secure->GetGET("tracking_again");

  $tablesid=$sid;
  if($drucken && !$tracking_again) {
    $this->app->User->SetParameter('versand_lasttracking', '');
    $this->app->User->SetParameter('versand_lasttracking_versand', '');
    $this->app->User->SetParameter('versand_lasttracking_lieferschein', '');
  }

  $versandmit= $this->app->Secure->GetPOST("versandmit");
  $trackingsubmit= $this->app->Secure->GetPOST("trackingsubmit");
  $versandmitbutton = $this->app->Secure->GetPOST("versandmitbutton");
  $tracking= trim($this->app->Secure->GetPOST("tracking"));
  $trackingBefore = $tracking;
  $trackingLink = $this->app->Secure->GetPOST("tracking_link");
  $trackingsubmitcancel= $this->app->Secure->GetPOST("trackingsubmitcancel");
  $retourenlabel = $this->app->Secure->GetPOST("retourenlabel");
  if($typ=="DHL" || $typ=="dhl")
    $versand = "dhl";
  else if($typ=="Intraship")
    $versand = "intraship";
  else $versand = $typ;

  //pruefe ob dpd im namen vorkommt
  $tracking = str_replace(' ','',$tracking);
  $tracking = str_replace('%','',$tracking);

  if($sid == "versand")
  {
    $this->app->Tpl->Set("TRACKINGMANUELL",'&nbsp;<input type="button" value="Trackingnummer direkt eingeben" onclick="window.location.href=\'index.php?module=versanderzeugen&action=frankieren&id='.$id.'&tracking_again=1\'" name="anders">&nbsp;');
    $projekt = $this->app->DB->Select("SELECT projekt FROM versand WHERE id='$id' LIMIT 1");
  }elseif($sid == "retoure")
  {
    $projekt = $this->app->DB->Select("SELECT projekt FROM retoure WHERE id='$id' LIMIT 1");
  }else{
    $projekt = $this->app->DB->Select("SELECT projekt FROM lieferschein WHERE id='$id' LIMIT 1");
  }
  $intraship_weightinkg = $this->app->DB->Select("SELECT intraship_weightinkg FROM projekt WHERE id='$projekt' LIMIT 1");


  if($trackingsubmit!="" || $trackingsubmitcancel!="")
  {
    //TrackingReplace
    $versandartenmodul = $this->app->DB->SelectArr("SELECT id, modul,bezeichnung, einstellungen_json FROM versandarten WHERE aktiv = 1 AND ausprojekt = 0 AND type = '".$this->app->DB->real_escape_string($typ)."' AND modul != '' AND (projekt = 0 OR projekt = '$projekt') ORDER BY projekt DESC LIMIT 1");

    if($versandartenmodul && @is_file(dirname(__FILE__).'/versandarten/'.$versandartenmodul[0]['modul'].'.php'))
    {
      $obj = $this->LoadVersandModul($versandartenmodul[0]['modul'], $versandartenmodul[0]['id']);
      if(!empty($obj) && method_exists($obj, 'TrackingReplace'))
      {
        $tracking = $obj->TrackingReplace($tracking);
      }
    }

  if($sid=="versand")
  {
    // falche tracingnummer bei DHL da wir in der Funktion PaketmarkeDHLEmbedded sind
    if((strlen($tracking) < 12 || strlen($tracking) > 21) && $trackingsubmitcancel=="" && ($typ=="DHL" || $typ=="Intraship"))
    {
      header("Location: index.php?module=versanderzeugen&action=frankieren&id=$id&land=$land&tracking_again=1");
      exit;
    }
    else
    {
/*      $versandartenmodul = $this->app->DB->SelectArr("SELECT id, modul,bezeichnung FROM versandarten WHERE aktiv = 1 AND ausprojekt = 0 AND type = '".$this->app->DB->real_escape_string($typ)."' AND modul != '' AND (projekt = 0 OR projekt = '$projekt') ORDER BY projekt DESC LIMIT 1");
      if($versandartenmodul && @is_file(dirname(__FILE__).'/versandarten/'.$versandartenmodul[0]['modul'].'.php'))
      {
        $obj = $this->LoadVersandModul($versandartenmodul[0]['modul'], $versandartenmodul[0]['id']);
        if(!empty($obj) && method_exists($obj, 'TrackingReplace'))
        {
          $tracking = $obj->TrackingReplace($tracking);
        }
      }
*/

      $trackingUser = !empty($this->app->User) && method_exists($this->app->User,'GetParameter')?
        $this->app->User->GetParameter('versand_lasttracking'):'';

      if(!empty($trackingUser) && in_array($trackingUser,[$trackingBefore,$tracking])) {
        $this->app->User->SetParameter('versand_lasttracking','');
        $this->app->User->SetParameter('versand_lasttracking_link','');
        $this->app->User->SetParameter('versand_lasttracking_versand', '');
        $this->app->User->SetParameter('versand_lasttracking_lieferschein', '');
      }

      $this->app->DB->Update("UPDATE versand SET versandunternehmen='$versand', tracking='$tracking',
          versendet_am=NOW(),versendet_am_zeitstempel=NOW(), abgeschlossen='1',logdatei=NOW() WHERE id='$id' LIMIT 1");
      if($lieferschein = $this->app->DB->Select("SELECT lieferschein FROM versand WHERE id = '$id' LIMIT 1")) {
        $this->app->erp->LieferscheinProtokoll($lieferschein,'Verarbeitung im Versandzentrum');
      }
      $rechnung = $this->app->DB->Select("SELECT rechnung FROM versand WHERE id = '$id' LIMIT 1");
      if($lieferschein && ($auftrag = $this->app->DB->Select("SELECT auftragid FROM lieferschein WHERE id = '$lieferschein'"))) {
        $this->app->erp->AuftragProtokoll($auftrag,'Verarbeitung im Versandzentrum');

        if(empty($rechnung)) {
          $rechnung = $this->app->DB->Select(
            sprintf(
              "SELECT id FROM rechnung WHERE auftragid = %d AND status <> 'storniert' AND belegnr <> '' LIMIT 1",
              $auftrag
            )
          );
          if($rechnung) {
            $this->app->DB->Update(
              sprintf(
                'UPDATE versand SET rechnung = %d WHERE id = %d AND rechnung = 0',
                $rechnung, $id
              )
            );
          }
        }
      }

      $projekt = $this->app->DB->Select("SELECT projekt FROM lieferschein WHERE id = '$lieferschein' LIMIT 1");
      if($lieferschein) {

        $this->PDFArchivieren('lieferschein', $lieferschein, true);
      }
      $proformaPrinted = false;
      $druckennachtracking = $this->app->erp->Projektdaten($projekt,'druckennachtracking');
      if($druckennachtracking
        && !($proformarechnung = $this->app->DB->Select(
          sprintf(
            'SELECT id FROM proformarechnung WHERE lieferschein = %d LIMIT 1',
            $lieferschein
          )
        )
        )
      ) {
        /** @var Versanderzeugen $versandObj */
        $versandObj = $this->LoadModul('versanderzeugen');
        if($versandObj !== null && method_exists($versandObj, 'checkPrintCreateProformaInvoice')) {
          $druckercode = $this->app->DB->Select("SELECT druckerlogistikstufe2 FROM projekt WHERE id='$projekt' LIMIT 1");

          if($druckercode <=0) {
            $druckercode = $this->app->erp->Firmendaten('standardversanddrucker'); // standard = 3 // 2 buchhaltung  // 1 empfang
          }
          $userversanddrucker = $this->app->DB->Select("SELECT standardversanddrucker FROM user WHERE id='".$this->app->User->GetID()."'");
          if($userversanddrucker>0) {
            $druckercode = $userversanddrucker;
          }
          $deliveryArr = $this->app->DB->SelectRow(
            sprintf('SELECT land FROM lieferschein WHERE id = %d', $lieferschein)
          );
          $country = $deliveryArr['land'];
          $versandObj->checkPrintCreateProformaInvoice($lieferschein,$country,$projekt,$druckercode,true);
          $proformaPrinted = $druckercode > 0;
        }
      }
      if($rechnung) {
        $this->PDFArchivieren('rechnung', $rechnung, true);
      }
      elseif($auftrag && $druckennachtracking
        && $this->app->DB->Select(
        sprintf(
          "SELECT id FROM auftrag WHERE id = %d AND art <> 'lieferung'",
          $auftrag
        )
        )
      ) {
        $rechnung = $this->WeiterfuehrenAuftragZuRechnung($auftrag);
        $this->BelegFreigabe('rechnung', $rechnung);
        $this->app->DB->Update(
          sprintf(
            'UPDATE versand SET rechnung = %d WHERE id = %d LIMIT 1',
            $rechnung, $id
          )
        );
        $this->app->DB->Update(
          sprintf(
            "UPDATE rechnung SET schreibschutz = 1, status = 'versendet' WHERE id = %d",
            $rechnung
          )
        );
        $this->PDFArchivieren('rechnung', $rechnung, true);
      }

      if($rechnung > 0 && $druckennachtracking
        && $this->app->DB->Select("SELECT count(id) FROM versand WHERE lieferschein = '$lieferschein'") <= 1) {
        $druckercode = $this->app->DB->Select("SELECT druckerlogistikstufe2 FROM projekt WHERE id='$projekt' LIMIT 1");

          if($druckercode <=0)
            $druckercode = $this->app->erp->Firmendaten("standardversanddrucker"); // standard = 3 // 2 buchhaltung  // 1 empfang

          $userversanddrucker = $this->app->DB->Select("SELECT standardversanddrucker FROM user WHERE id='".$this->app->User->GetID()."'");
          if($userversanddrucker>0)
            $druckercode = $userversanddrucker;

        $this->app->erp->BriefpapierHintergrundDisable($druckercode);

        $addressId = $this->app->DB->Select("SELECT `adresse` FROM `rechnung` WHERE `id` = '{$rechnung}' LIMIT 1");
        $printInvoice = $this->app->DB->Select("SELECT `rechnung_papier` FROM `adresse` WHERE `id` = '{$addressId}' LIMIT 1");
        $amountPrintedInvoices = $this->app->DB->Select("SELECT `rechnung_anzahlpapier` FROM `adresse` WHERE `id` = '{$addressId}' LIMIT 1");
        if($printInvoice === '1' && $amountPrintedInvoices > 0){
          if(class_exists('RechnungPDFCustom'))
          {
            $Brief = new RechnungPDFCustom($this->app,$projekt);
          }else{
            $Brief = new RechnungPDF($this->app,$projekt);
          }
          $Brief->GetRechnung($rechnung);
          $tmpfile = $Brief->displayTMP();

          for($i = 1; $i <= $amountPrintedInvoices; $i++) {
            $this->app->printer->Drucken($druckercode,$tmpfile);
          }
        } else {
          $autodruckrechnungmenge = $this->app->erp->Projektdaten($projekt,"autodruckrechnungmenge");
          $autodruckrechnung= $this->app->erp->Projektdaten($projekt,"autodruckrechnung");

          if($autodruckrechnungmenge < 0)$autodruckrechnungmenge = 1;

          if($autodruckrechnung > 0)
          {
            if(class_exists('RechnungPDFCustom'))
            {
              $Brief = new RechnungPDFCustom($this->app,$projekt);
            }else{
              $Brief = new RechnungPDF($this->app,$projekt);
            }
            $Brief->GetRechnung($rechnung);
            $tmpfile = $Brief->displayTMP();

            for($i = 1; $i <= $autodruckrechnungmenge; $i++) {
              $this->app->printer->Drucken($druckercode,$tmpfile);
            }
          }
        }


        // Print additional invoices for export
        $exportdruckrechnung = $this->app->erp->Projektdaten($projekt,"exportdruckrechnung");
