        //$fields['vorlage_'.$k] = array('type'=>'link','nodoublepoint'=>1,'bezeichnung' => '&bull;','link'=>'<a onclick="$(\'#versand_klaergrund\').val($(this).html());">'.$v['problemcase'].'</a>');
      }
      $fields = array('klaergrund' => array('type'=>'radiobutton','optionen'=>$klaeroptions));
      if(!empty($klaeroptions)){
        $this->app->YUI->AddSimpleForm('versand', $fields,
          array('editbutton' => $this->app->Secure->GetGET('id'), 'nocreate' => true, 'edittext' => 'Kl&auml;rfall anlegen', 'legend' => 'Grund', 'title' => 'Einstellungen', 'functionaftersave' => 'VersanderzeugenKlaerfallfunctionaftersave'
          , 'addbuttonafter' => '$(\'input[name="drucken"]\').parent().find(\'input\').last()', 'onsave' => 'window.location.href=\'index.php?module=versanderzeugen&action=offene\''), $obj);
      }
    }
  }
}

//@refactor versanddiestleister Modul
function ExtractStreetnumber($street)
{
  $address = trim($street);
  $number = preg_replace('/^.*?(\d+).*$/i', '$1', $address);
  // suche erstes vorkommen
  $pos = strpos($address,$number);
  if($pos === 0)
  {
    $result = substr($address, 0, strpos($address ,' '));
  }else{
    $result = substr  ( $address, $pos);
  }
  return $result;
}
