    }

    $mhd_menge = $menge;
    $charge_menge = $menge;
    $extraorder = '';
    if($mhd=='1' && $mhdauslagern) {
      $timeout=0;
      while($mhd_menge > 0 && $timeout < 1000) {
        $timeout++;
        $this->app->erp->RunHook('LagerAuslagernRegalMHDCHARGESRN_MHD', 7,$artikel, $regal, $menge, $dokumenttyp,$dokumentid,$dokumentposid, $extraorder);
