  function GetUSTEU($ust=false)
  {
    $isoGreece = 'GR';
    if($ust){
      $isoGreece = 'EL';
    }

    $checklaender = $this->app->DB->Select('SELECT id FROM laender LIMIT 1');
    if($checklaender > 0 )
    {
      $laender = $this->app->DB->SelectArr("SELECT iso FROM laender WHERE eu=1 ORDER by iso");
      $ret = null;
      foreach($laender as $land) {
        if($land['iso'] === 'GR'){
          $ret[] = $isoGreece;
        }else{
          $ret[] = $land['iso'];
        }
      }
      return $ret;
    }

    return
        array('DE','BE','IT','RO',
          'BG','LV','SE',
          'DK','LT','SK',
          'DE','LU','SI',
          'EE','MT','ES',
          'FI','NL','CZ',
          'FR','AT','HU',
          $isoGreece,'PL',
          'IE','PT','CY','HR');
  }


