      public function insertDefaultUserRights($userId)
      {
        if($userId <= 0) {
          return;
        }
        foreach(['login','logout','start','startseite','settings'] as $action) {
          $this->app->DB->Update(
            sprintf(
              "INSERT INTO `userrights` (`user`, `module`, `action`, `permission`) VALUES (%d, 'welcome', '%s', 1)",
              $userId, $action
            )
          );
        }
      }

