            sprintf(
              'SELECT IFNULL(SUM(tp.menge*tp.preis),0) 
                FROM `%s` AS tp
                LEFT JOIN artikel a ON a.id=tp.artikel 
                WHERE a.porto!=1 AND (a.rabatt!=1 OR a.rabatt IS NULL) AND tp.`%s` = %d
              ',
